jQuery(document).ready(function($) {
    $('#admin-img-file').change(function() {
        var formData = new FormData();
            for (var i = 0; i < this.files.length; i++) {
                var f = this.files[i];
                formData.append('image', f);
            }
            formData.append('token', infoData.Token);
            formData.append('apiType', 'ali,');
            $.ajax({
                url: 'https://htm.fun/api/upload',
                type: 'POST',
                processData: false,
                contentType: false,
                data: formData,
                success: function(res) {
                    $('textarea[name="content"]').insertAtCaret('<img class="aligncenter" src="' + res.data.url.distribute + '" />');
                    $("html").find("iframe").contents().find("body").append('<img class="aligncenter" src="' + res.data.url.distribute + '" />')
                }
            })
        
    });
    $.fn.extend({
        insertAtCaret: function(myValue) {
            var $t = $(this)[0];
            if (document.selection) {
                this.focus();
                sel = document.selection.createRange();
                sel.text = myValue;
                this.focus()
            } else if ($t.selectionStart || $t.selectionStart == "0") {
                var startPos = $t.selectionStart;
                var endPos = $t.selectionEnd;
                var scrollTop = $t.scrollTop;
                $t.value = $t.value.substring(0, startPos) + myValue + $t.value.substring(endPos, $t.value.length);
                this.focus();
                $t.selectionStart = startPos + myValue.length;
                $t.selectionEnd = startPos + myValue.length;
                $t.scrollTop = scrollTop
            } else {
                this.value += myValue;
                this.focus()
            }
        }
    })
})